// webkitчФиTypescriptхЮЛхогшиА
declare global {
  interface Window {
    webkitAudioContext: AudioContext;
  }
}

const ReactAudioTest: React.FunctionComponent = () => {
  // щЯ│щЗПя╝ЪхИЭцЬЯхАд25
  const [volume, setVolume] = React.useState(25);
  // хЖНчФЯчК╢цЕЛя╝ЪхИЭцЬЯхАдfalse
  const [playingFlag, setPlayingFlag] = React.useState(false);
  // AudioContextя╝ЪхИЭцЬЯхАдundefined
  const [audioContext, setAudioContext] = React.useState<
    AudioContext | undefined
  >(undefined);
  // щЯ│ц║РуВ╜уГ╝уВ╣я╝ЪхИЭцЬЯхАдundefined
  const [source, setSourceState] = React.useState<
    AudioBufferSourceNode | undefined
  >(undefined);
  // щЯ│щЗПшк┐чпАя╝ЪхИЭцЬЯхАдundefined
  const [gainState, setGainState] = React.useState<GainNode | undefined>(
    undefined
  );

  React.useEffect(() => {
    // хИЭцЬЯхМЦ
    const newAudioContext = new (window.AudioContext ||
      window.webkitAudioContext)();
    setAudioContext(newAudioContext);
    const gainNode = newAudioContext.createGain();
    gainNode.gain.value = volume / 100;
    setGainState(gainNode);

    return () => {
      // unmountцЩВуБох╛МхзЛцЬл
      if (source) {
        source.buffer = null; // уГбуГвуГкуГкуГ╝уВпщШ▓цнв
      }
      if (newAudioContext) {
        newAudioContext.close();
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  React.useEffect(() => {
    // щЯ│щЗПхдЙцЫ┤
    if (gainState) {
      gainState.gain.value = volume / 100;
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [volume]);

  // уВ╣уГЮуГЫуБкуБйуБзуБпш┐╖цГСуБкшЗкхЛХхЖНчФЯуВТщШ▓уБРчЫочЪДуБз
  // уГжуГ╝уВ╢уБоцУНф╜ЬуБлф╝┤уБЖхЖНчФЯуБзуБЧуБЛщЯ│уВТхЖНчФЯуБзуБНуБкуБД
  const onClickToStartAndStop = React.useCallback(() => {
    if (audioContext && gainState) {
      if (playingFlag) {
        // хБЬцнв
        if (source) {
          source.stop();
          source.buffer = null; // уГбуГвуГкуГкуГ╝уВпщШ▓цнв
        }
      } else {
        // хЖНчФЯ
        const channels = 1; // уГвуГОуГйуГл
        const frameCount = audioContext.sampleRate * 2.0; // 2чзТ

        const myArrayBuffer = audioContext.createBuffer(
          channels,
          frameCount,
          audioContext.sampleRate
        );

        for (let channel = 0; channel < channels; channel += 1) {
          // уГЫуГпуВдуГИуГОуВдуВ║чФЯцИР
          const nowBuffering = myArrayBuffer.getChannelData(channel);
          for (let i = 0; i < frameCount; i += 1) {
            nowBuffering[i] = Math.random() * 2 - 1;
          }
        }
        const buffersource = audioContext.createBufferSource();
        setSourceState(buffersource);
        buffersource.buffer = myArrayBuffer;
        buffersource.loop = true;
        buffersource.connect(gainState).connect(audioContext.destination);
        buffersource.start();
      }
      setPlayingFlag(!playingFlag);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [playingFlag, audioContext, gainState]);

  if (!audioContext || !gainState) {
    return (
      <React.Fragment>
        <CircularProgress />
        loading...
      </React.Fragment>
    );
  }

  return (
    <div className="text-center h-fit w-full">
      <Grid container>
        <Grid item></Grid>
        <Grid item>
          <VolumeDown />
        </Grid>
        <Grid item xs={10}>
          <Slider
            min={0}
            max={100}
            defaultValue={50}
            value={volume}
            onChange={(__, newValue) => {
              if (typeof newValue === 'number') {
                setVolume(newValue);
              }
            }}
            valueLabelDisplay="auto"
            aria-labelledby="continuous-slider"
          />
        </Grid>
        <Grid item>
          <VolumeUp />
        </Grid>
        <Grid item></Grid>
      </Grid>

      <Button
        variant="outlined"
        color={playingFlag ? 'info' : 'primary'}
        onClick={onClickToStartAndStop}
      >
        уГЫуГпуВдуГИуГОуВдуВ║{playingFlag ? 'хБЬцнв' : 'хЖНчФЯ'}
      </Button>
    </div>
  );
};

export { ReactAudioTest };
\`\`\`
`;

