use std::ops::Rem;

pub enum FizzBuzz {
  Fizz,
  Buzz,
  FizzBuzz,
  Number(String),
}

impl fmt::Display for FizzBuzz {
  fn fmt(&self, f: &mut fmt::Formatter) -> fmt::Result {
    match self {
      FizzBuzz::Fizz => write!(f, "Fizz"),
      FizzBuzz::Buzz => write!(f, "Buzz"),
      FizzBuzz::FizzBuzz => write!(f, "FizzBuzz"),
      FizzBuzz::Number(x) => write!(f, "{x}"),
